    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
       var remedioSelecionado = CarregarUmRemedioDaTabela();
        
       String nome = txtCampoNomeMedicamento.getText();
        String dosagem = txtCampoDosagemMedicamento.getText();

        new RemedioService().atualizarRemedio(nome, dosagem, remedioSelecionado);
        limpaCampos();
        carregarTabela();
    }//GEN-LAST:event_btnAlterarActionPerformed

    private void bntSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntSalvarActionPerformed
        String nome = txtCampoNomeMedicamento.getText();
        String dosagem = txtCampoDosagemMedicamento.getText();

        new RemedioService().cadastrarRemedio(nome,dosagem);
        
        limpaCampos();
        carregarTabela();
        
    }//GEN-LAST:event_bntSalvarActionPerformed

