    private void bntBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntBuscarActionPerformed

        RemedioModel remedioRetornado = new RemedioRepository().readMedicamento(this.CarregarUmRemedioDaTabela());
        
        labelNomeRemedio.setText(remedioRetornado.getNome());
        labelDosagemRemedio.setText(remedioRetornado.getDosagem());
        
    }//GEN-LAST:event_bntBuscarActionPerformed

    private void btnDeletarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeletarActionPerformed
       var remedio = CarregarUmRemedioDaTabela();
       new RemedioService().deletarRemedio(remedio);
       limpaCampos();
       carregarTabela();
    }//GEN-LAST:event_btnDeletarActionPerformed

