    private  RemedioModel CarregarUmRemedioDaTabela(){
         int linhaSelecionada = tabelaRemedios.getSelectedRow();
            
         int codigoSelecionado =Integer.parseInt(tabelaRemedios.getModel().getValueAt(linhaSelecionada, 0).toString());
        String nomeSelecionado = tabelaRemedios.getModel().getValueAt(linhaSelecionada, 1).toString();
        String dosagemSelecionada = tabelaRemedios.getModel().getValueAt(linhaSelecionada, 2).toString();
        
        txtSelecionado.setText(nomeSelecionado);
        
        RemedioModel remedio =  new RemedioModel(codigoSelecionado, nomeSelecionado, dosagemSelecionada);
        return remedio;
    }
    
    private void limpaCampos(){
        txtCampoNomeMedicamento.setText("");
        txtCampoDosagemMedicamento.setText("");
        txtSelecionado.setText("");
    }
    
    private void carregarTabela(){
            tabelaRemedios.setModel(new RemedioService().carregarRemedios());
    }
